// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "./PlayerManager.sol";
import "./PointsRules.sol";

/// @title PointsCalculator - Calculates points for player interactions
/// @notice Handles all point calculations for player interactions and activities
contract PointsCalculator is Initializable, OwnableUpgradeable, UUPSUpgradeable {
    PlayerManager public playerManager;
    PointsRules public pointsRules;

    event PointsCalculated(address indexed player, uint32 newScore);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /// @notice Initializes the PointsCalculator contract
    /// @param _playerManager Address of the PlayerManager contract
    /// @param _pointsRules Address of the PointsRules contract
    /// @param _owner Address of the contract owner
    function initialize(
        address _playerManager,
        address _pointsRules,
        address _owner
    ) external initializer {
        __Ownable_init(_owner);
        __UUPSUpgradeable_init();
        playerManager = PlayerManager(_playerManager);
        pointsRules = PointsRules(_pointsRules);
    }

    /// @notice Authorizes an upgrade to a new implementation
    /// @param newImplementation Address of the new implementation
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    /// @notice Calculates points for player interaction
    /// @param _player1 Address of the first player
    /// @param _player2 Address of the second player
    function calculateInteractionPoints(address _player1, address _player2) 
        external 
        onlyOwner 
    {
        (uint32 score1,,,) = playerManager.getPlayer(_player1);
        (uint32 score2,,,) = playerManager.getPlayer(_player2);
        (uint32 newScore1, uint32 newScore2) = pointsRules.calculateInteraction(
            score1,
            score2
        );

        playerManager.updateScore(_player1, newScore1);
        playerManager.updateScore(_player2, newScore2);

        emit PointsCalculated(_player1, newScore1);
        emit PointsCalculated(_player2, newScore2);
    }

    /// @notice Calculates points for meetup attendance
    /// @param _player Address of the player
    function calculateMeetupPoints(address _player) 
        external 
        onlyOwner 
    {
        (uint32 currentScore,,,) = playerManager.getPlayer(_player);
        uint32 newScore = pointsRules.calculateMeetup(currentScore);

        playerManager.updateScore(_player, newScore);
        emit PointsCalculated(_player, newScore);
    }

    /// @notice Calculates accrued points over time
    /// @param _player Address of the player
    function calculateAccruedPoints(address _player) 
        external 
        onlyOwner 
    {
        (uint32 score, uint32 lastPointAccrualTime,,) = playerManager.getPlayer(_player);

        uint32 newScore = pointsRules.calculateAccrued(
            score,
            lastPointAccrualTime,
            uint32(block.timestamp)
        );

        playerManager.updateScore(_player, newScore);
        emit PointsCalculated(_player, newScore);
    }
}

contracts/PointsRules.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

/// @title PointsRules - Defines rules for point calculations
/// @notice Contains the logic for calculating points in various game scenarios
contract PointsRules is Initializable, OwnableUpgradeable, UUPSUpgradeable {
    uint8 public interactionPoints;
    uint8 public meetupPoints;
    uint8 public pointsPerTenHours;
    uint32 public maxScore;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /// @notice Initializes the PointsRules contract
    /// @param _owner Address of the contract owner
    function initialize(address _owner) external initializer {
        __Ownable_init(_owner);
        __UUPSUpgradeable_init();
        interactionPoints = 2;
        meetupPoints = 4;
        pointsPerTenHours = 1;
        maxScore = 1000000;
    }

    /// @notice Authorizes an upgrade to a new implementation
    /// @param newImplementation Address of the new implementation
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    /// @notice Calculates points for player interaction
    /// @param score1 Score of first player
    /// @param score2 Score of second player
    /// @return uint32 New score for first player
    /// @return uint32 New score for second player
    function calculateInteraction(uint32 score1, uint32 score2) 
        external 
        view 
        returns (uint32, uint32) 
    {
        if (score1 > score2) {
            return (
                score1 + interactionPoints,
                score2 > interactionPoints ? score2 - interactionPoints : 0
            );
        } else {
            return (
                score1 > interactionPoints ? score1 - interactionPoints : 0,
                score2 + interactionPoints
            );
        }
    }

    /// @notice Calculates points for meetup attendance
    /// @param currentScore Current score of the player
    /// @return uint32 New score after meetup
    function calculateMeetup(uint32 currentScore) 
        external 
        view 
        returns (uint32) 
    {
        uint32 newScore = currentScore + meetupPoints;
        require(newScore >= currentScore, "Score overflow");
        return newScore;
    }

    /// @notice Calculates points accrued over time
    /// @param currentScore Current score of the player
    /// @param lastAccrualTime Last time points were accrued
    /// @param currentTime Current timestamp
    /// @return uint32 New score after accrual
    function calculateAccrued(
        uint32 currentScore,
        uint32 lastAccrualTime,
        uint32 currentTime
    ) 
        external 
        view 
        returns (uint32) 
    {
        uint32 tenHoursPassed = (currentTime - lastAccrualTime) / 36000;
        uint32 pointsToAdd = tenHoursPassed * pointsPerTenHours;
        uint32 newScore = currentScore + pointsToAdd;
        require(newScore >= currentScore && newScore <= maxScore, "Invalid score");
        return newScore;
    }
}

contracts/PointsSystem.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;

import "./libraries/GameLibrary.sol";
import "./storage/GameStorage.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract PointsSystem is GameStorage, UUPSUpgradeable {
    using GameLibrary for uint32;

    error ScoreOverflow();
    error InvalidScore();

    function _authorizeUpgrade(address newImplementation) internal override {}

    function calculateInteraction(
        address player1,
        address player2
    ) external returns (uint32, uint32) {
        Player storage p1 = _players[player1];
        Player storage p2 = _players[player2];

        (uint32 newScore1, uint32 newScore2) = GameLibrary.calculatePoints(
            p1.score,
            p2.score
        );

        if (newScore1 > GameLibrary.MAX_SCORE || newScore2 > GameLibrary.MAX_SCORE) 
            revert ScoreOverflow();

        p1.score = newScore1;
        p2.score = newScore2;

        emit PlayerAction(player1, 1, newScore1);
        emit PlayerAction(player2, 1, newScore2);

        return (newScore1, newScore2);
    }
}

